import React, { useState } from 'react';
import { useSpring, useTrail, animated } from '@react-spring/web';
import { useInView } from 'react-intersection-observer';
// import backgroundImage from "../assets/hero.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserLarge, faUtensils, faCartPlus, faHeadset } from '@fortawesome/free-solid-svg-icons';
import mealImage from "../";

const Home = () => {
    const [ref1, inView1] = useInView({ triggerOnce: true, threshold: 0.1 });

    const services = [
        { 
            title: 'Master Chefs', 
            description: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam', 
            icon: <FontAwesomeIcon icon={faUserLarge} /> 
        },
        { 
            title: 'Quality Food', 
            description: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam', 
            icon: <FontAwesomeIcon icon={faUtensils} /> 
        },
        { 
            title: 'Online Order', 
            description: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam', 
            icon: <FontAwesomeIcon icon={faCartPlus} /> 
        },
        { 
            title: '24/7 Service', 
            description: 'Diam elitr kasd sed at elitr sed ipsum justo dolor sed clita amet diam', 
            icon: <FontAwesomeIcon icon={faHeadset} /> 
        }
    ];

    // Updated trail animation
    const trail = useTrail(services.length, {
        from: { opacity: 0, transform: 'scale(0.8) rotateY(90deg)' },
        to: { 
            opacity: inView1 ? 1 : 0, 
            transform: inView1 ? 'scale(1) rotateY(0deg)' : 'scale(0.8) rotateY(90deg)' 
        },
        config: { tension: 250, friction: 30 },
    });

    return (
        <div>
            {/* Hero Section */}
            <div
                className="min-h-[95vh] flex flex-col items-center justify-center bg-cover bg-center relative"
                style={{ backgroundImage: `url(${backgroundImage})` }}
            >
                {/* Blackish Overlay */}
                <div className="absolute inset-0 bg-black opacity-90"></div>

                {/* Action Button */}
                <div className="absolute inset-y-0 left-0 z-10 max-w-full text-white px-8 lg:px-16 pt-12 lg:pt-28"> {/* Adjusted pt-* */}
                    <div className="relative">
                        <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold leading-tight mb-6">
                            Enjoy Our Delicious Meal
                        </h1>
                        <p className="text-lg sm:text-xl md:text-2xl lg:text-3xl mb-8">
                            Tempor erat elitr rebum at clita. Diam dolor diam ipsum sit.<br />
                            Aliqu diam amet diam et eos.
                            Clita erat ipsum et lorem et sit,<br /> sed stet lorem sit clita duo justo magna dolore erat amet.
                        </p>
                        <button className="bg-yellow-500 text-black px-6 py-3 rounded hover:bg-yellow-600">
                            BOOK A TABLE
                        </button>
                    </div>
                </div>

                {/* Image Section with increased size */}
                <div className="absolute bottom-0 right-0 mr-8 lg:mr-16 w-full max-w-lg lg:max-w-2xl flex justify-end">
                    <img
                        src={mealImage}
                        alt="Delicious Meal"
                        className="w-full h-auto object-cover rounded-lg shadow-lg opacity-80 animate-spin-slow"
                    />
                </div>
            </div>

            {/* Service Section */}
            <div ref={ref1} className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 px-8">
                {trail.map((style, index) => (
                    <animated.div
                        key={index}
                        style={style}
                        className="bg-white p-6 rounded-lg shadow-lg flex flex-col items-center transition-colors duration-300 hover:bg-orange-500 group"
                    >
                        <div className="text-4xl text-orange-500 mb-4 group-hover:text-white">{services[index].icon}</div>
                        <h3 className="text-xl font-semibold group-hover:text-white">{services[index].title}</h3>
                        <p className="mt-2 text-center group-hover:text-white">{services[index].description}</p>
                    </animated.div>
                ))}
            </div>
        </div>
    );
};

export default Home;
